int MoveDir(char c) => c == '(' ? 1 : c == ')' ? -1 : throw new Exception($"invalid input: \'{c}\'");

int SolveFirst(string input) => input.Sum(c => MoveDir(c));

int SolveSecond(string input)
{
  int floor = 0;
  for(int i = 0; i < input.Length; ++i)
  {
    floor += MoveDir(input[i]);

    if(floor == -1)
      return i+1 ;
  }

  throw new Exception("input has no solution");
}

