int SolveFirst(string input) => input.Aggregate(0, (acc, c) => acc + (c == '(' ? 1 : c == ')' ? -1 : 0));

int SolveSecond(string input)
{
  int reachBasementInstr = 0;

  input.Aggregate((index: 0, floor: 0), (acc, c) =>
  {
    if (c == '(') ++acc.floor;
    if (c == ')') --acc.floor;
    if (acc.floor == -1 && reachBasementInstr == 0) reachBasementInstr = acc.index + 1;

    return (++acc.index, acc.floor);
  });

  return reachBasementInstr;
}

